sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var noNewValue            = newValue === '';
	var notNewRecord          = isLoading && !g_form.isNewRecord();
	var whatNeededFormValue   = g_form.getValue('u_what_needed');
	var mapForNewValueHandler = {
		types : [
			{type: "hr",         title: "Human Resources"},
			{type: "facilities", title: "Facilities"},
			{type: "legal",      title: "Legal"},
		],
	};

	var __helperFormAddingOptions = function(selectOptionName, selectOptionTitle){
		g_form.clearOptions('u_what_needed');
		g_form.addOption('u_what_needed', selectOptionName.concat('1'), selectOptionTitle.concat(' 1'));
		g_form.addOption('u_what_needed', selectOptionName.concat('2'), selectOptionTitle.concat(' 2'));
		g_form.addOption('u_what_needed', 'other', 'Other');
	};

	if (noNewValue) {
		return;
	}

	mapForNewValueHandler.types.find(function(item){
		if(item.type === newValue) {
			__helperFormAddingOptions(item.type, item.title);
		}
	});

	if(notNewRecord){
		g_form.setValue('u_what_needed', whatNeededFormValue);
	}

}
