sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		this.now = new GlideDateTime();
    },
	isDatePast: function(date) {
		var givenDate = new GlideDateTime(date);

		return givenDate.before(this.now);
	},
	isDateCurrentDay: function(date) {
		var givenDate = new GlideDateTime(date);

		return this.now.getLocalDate().compareTo(givenDate.getLocalDate()) === 0;
	},
	abortActionWithMessage: function(action, message) {
		gs.addErrorMessage(message);
        action.setAbortAction(true);
	},
    type: 'NeedItUtils'
};
